import { BaiduMap } from 'vue-baidu-map-3x';
import { ref, nextTick } from 'vue';
const center = ref({
  lng: 116.404,
  lat: 39.915
});
let zoom = ref(15);

const syncCenterAndZoom = e => {
  const {
    lng,
    lat
  } = e.target.getCenter();
  zoom.value = e.target.getZoom();
  nextTick(() => {
    center.value.lng = lng;
    center.value.lat = lat;
  });
};
